CREATE TABLE page (id BIGSERIAL, title VARCHAR(100) DEFAULT '' NOT NULL, body TEXT DEFAULT '' NOT NULL, category VARCHAR(32) DEFAULT '' NOT NULL, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, slug VARCHAR(255), PRIMARY KEY(id));
CREATE TABLE staff (id BIGSERIAL, name VARCHAR(100) DEFAULT '' NOT NULL, kana VARCHAR(100) DEFAULT '' NOT NULL, comment TEXT DEFAULT '' NOT NULL, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE UNIQUE INDEX page_sluggable ON page (slug);
